import React, {useState, useRef} from 'react';
import CloseIcon from '@material-ui/icons/Close';
import axios from 'axios';

function Advance({setTrig, worker}) {


    const [month, setMonth] = useState('');
    const amtRef = useRef();



    const handleMonth = (e) => {
        setMonth(e.target.value);
    }



    async function handleAdv() {

        const AdvanceAmt = {
	
			adv: amtRef.current.value		
        }

        let id = worker._id;

        await axios.post(`http://localhost:5000/users/update${month}Adv/${id}`, AdvanceAmt)
                .then(res => {console.log(res.data)})
                .catch(err => {console.log(err)})
    } 


    return (
        <div className="cw">
            
            <CloseIcon className="profile__back" onClick={() => setTrig(false)} style={{fontSize: "35px"}} />
            
            <div className="cw__container" style={{display: "flex", justifyContent: "center", width: "40vw", alignItems: "center"}}>
                <div className="att__content">
                    <h2 >{worker.name}</h2>

                    <h3>Month</h3>
                    <div >
                            <select value={month} onChange={handleMonth} name="site" className="cw__dropdown" style={{marginBottom: "20px"}}>
                                <option >Jan</option>
                                <option >Feb</option>
                                <option >Mar</option>
                                <option >Apr</option>
                                <option >May</option>
                                <option >Jun</option>
                                <option >Jul</option>
                                <option >Aug</option>
                                <option >Sep</option>
                                <option >Oct</option>
                                <option >Nov</option>
                                <option >Dec</option>
                            </select>
                    </div>

                    <h3>Advance Amount</h3>
                    <input type="number" className="cw__inp" ref={amtRef} style={{marginBottom: "40px", height: "40px"}} />
                    
                    <button onClick={handleAdv}  className="cw__btn" >Add Advance</button>
                    
                </div>

            </div>
        </div>
    )
}

export default Advance
