import React, {useEffect, useState} from 'react';
import Navbar from '../Components/Navbar';
import './MonthlyReport.css';
import axios from 'axios';


function MonthlyReport() {

    const [workers, setWorkers] = useState([]);
    const [report, setReport] = useState();
    const [month, setMonth] = useState("Jan");
    let Month = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];


    const [next, setNext] = useState(false);
    const [prev, setPrev] = useState(false);
    const [ind, setInd] = useState(0);

    

    const [sites, setSites] = useState([]);
    const [sortSiteVal, setSortSiteVal] = useState('All');
    const [siteCheck, setSiteCheck] = useState(false);



    useEffect(() => {

        if (ind===0) {
            setPrev(true);
        } else {
            setPrev(false);
        }

        if(ind===11) {
            setNext(true);
        }else{
            setNext(false);
        }

        // switch(ind){
        //     case 0:
        //         setMonth("Jan");
        //         break;
            
        //     case 1:
        //         setMonth("Feb");
        //         break;
        //     case 2:
        //         setMonth("Mar");
        //         break;
        //     case 3:
        //         setMonth("Apr");
        //         break;
        //     case 4:
        //         setMonth("May");
        //         break;
        //     case 5:
        //         setMonth("Jun");
        //         break;
        //     case 6:
        //         setMonth("Jul");
        //         break;
        //     case 7:
        //         setMonth("Aug");
        //         break;
        //     case 8:
        //         setMonth("Sep");
        //         break;
        //     case 9:
        //         setMonth("Oct");
        //         break;
        //     case 10:
        //         setMonth("Nov");
        //         break;
        //     case 11:
        //         setMonth("Dec");
        //         break;
    
        //     default:
        //         break;
        // }
        
        async function getData() {

            await axios.get("http://localhost:5000/sites")
                    .then(res => setSites(res.data))
                    .catch(err => console.log(err))

            // if(sortSiteVal === "All"){
            //     await axios.get("http://localhost:5000/users/")
            //         .then(res => {setWorkers(res.data)})
            //         .catch(err => {console.log(err)})
            // } else {
                
            //     const siteVal = {
            //         site: sortSiteVal
            //     }
            //     await axios.post("http://localhost:5000/users/siteSort", siteVal)
            //         .then(res => {setWorkers(res.data)})
            //         .catch(err => {console.log(err)})
            // }

            await axios.get("http://localhost:5000/users/")
                        .then(res => {setWorkers(res.data)})
                        .catch(err => {console.log(err)})
            
            setMonth(Month[ind]);
    
            await axios.get(`http://localhost:5000/users/${month}Report`)
                    .then(res => setReport(res.data) )
                    .catch(err => console.log(err))
        }
        
       getData();
            
    }, [ind, month, Month])   

    

    const handlePrev = () => {
        
        let i = ind  - 1;
        setInd(i);
    }

    const handleNext = () => {
        

        let k = ind  + 1;
        setInd(k);
    }



    return (
        <div className="workers">
            <div className="workers__banner" style={{height: "350px"}}>
                <Navbar />
            </div>

            <div className="workers__title">
                <div className="mr__report-title" >
                    <button disabled={prev} onClick={handlePrev} className="mr__title-arrow"  style={{fontSize: "15px", marginRight: "20px", marginTop: "10px", color : "#9ae3e6"}} > Prev </button>
                    <h2>{month}'s Report</h2>
                    <button disabled={next} onClick={handleNext} className="mr__title-arrow"  style={{fontSize: "15px", marginLeft: "20px", marginTop: "10px", color : "#9ae3e6"}} >Next</button>
                    
                </div>
                
                <div >
                        <select name="site" value={sortSiteVal} onChange={(e) => setSortSiteVal(e.target.value)} className="workers__dropdown">
                            <option >All</option>
                            {sites.map(site => (
                                    <option >{site.siteName}</option>
                                ))}
                        </select>
                </div>
            </div>

            <div className="workers__container" style={{overflowX: "scroll"}} >
                <div className="workers__content" >
                                       

                    <div >
                        <table className="workers__table">
                            <thead className="mr__content-header">
                            
                                <h3 style={{marginRight: "180px"}}>Name</h3>
                                <h3 style={{marginRight: "120px"}}>Type</h3>
                                <h3>Site</h3>
                                <h3>Phone</h3>
                                <h3 style={{marginRight: "120px"}}>DOJ</h3>
                                <h3>UAN No</h3>
                                <h3>PF No</h3>
                                <h3>ESIC</h3>
                                <h3>Presesnt Days</h3>
                                <h3>OT Hours</h3>
                                <h3>Wages Rate</h3>
                                <h3>DA/SA Rate</h3>
                                <h3>OT Rate</h3>
                                <h3>Basic Wages</h3>
                                <h3>DA Wages</h3>
                                <h3>OT Amt</h3>
                                <h3>HRA</h3>
                                <h3>Total Wages</h3>
                                <h3>PF</h3>
                                <h3>ESIC</h3>
                                <h3>PT</h3>
                                <h3>LWF</h3>
                                <h3>Other Deduction</h3>
                                <h3>Total Deduction</h3>
                                <h3>Payment</h3>
                                <h3>Bank</h3>
                                <h3 style={{marginRight: "140px"}}>Acc No</h3>
                                <h3 style={{marginRight: "110px"}}>IFSC</h3>
                                <h3 style={{marginRight: "5px"}}>Aadhar No</h3>
                            
                            </thead>
                            {/* <tbody style={{overflowY: "scroll"}}>
                                {workers.map(worker => {

                                    let wReport;

                                    for(let i=0; i<report?.length; i++){
                                        if(report[i][1] === worker._id){
                                            wReport = report[i][0];
                                        }
                                    }
                                    
                                    return(
                                        <tr className="mr__content-worker">
                                            <div className="mr__content-data" style={{marginRight: "75px", justifyContent: "flex-start"}} ><p >{worker.name}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "36px"}}><p >{worker.type}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "20px"}}><p >{wReport?.site==="NA" ? worker.site : wReport?.site}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "15px"}}><p >{worker.phoneNo}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "50px"}}><p >{worker.doj.substring(0, 10)}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "30px"}}><p >{worker.uanNo}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "20px"}}><p >{worker.pfNo}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "50px"}}><p >{worker.esic}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "70px"}}><p >{wReport?.presentDays}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "60px"}}><p >{wReport?.otHrs}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "70px"}}><p >{worker.wagesRate}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "60px"}}><p >{worker.daRate}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "50px"}}><p >{worker.otRate}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "80px"}}><p >{wReport?.basicWages}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "40px"}}><p >{wReport?.daWages}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "30px"}}><p >{wReport?.otAmt}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "40px"}}><p >{wReport?.hra}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "40px"}}><p >{wReport?.totalWages}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "0px"}}><p >{wReport?.pf}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "0px"}}><p >{wReport?.esic}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "0px"}}><p >{wReport?.pt}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "50px"}}><p >{wReport?.lwf}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "120px"}}><p >{wReport?.adv}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "80px"}}><p >{wReport?.totDeduction}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "35px"}}><p >{wReport?.fPay}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "25px"}}><p >{worker.bank}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "70px"}}><p >{worker.accountNo}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "55px"}}><p >{worker.ifsc}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "50px"}}><p >{worker.aadharNo}</p></div>                                    
                                            
                                    </tr>)
                                })}
                            
                            </tbody> */}
                            <tbody style={{overflowY: "scroll"}}>
                                {workers.map(worker => {

                                    let wReport;

                                    for(let i=0; i<report?.length; i++){
                                        if(report[i][1] === worker._id){
                                            wReport = report[i][0];
                                        }
                                    }

                                    if(sortSiteVal === "All" || wReport?.site===sortSiteVal){
                                            setSiteCheck(true);
                                    }
                                    
                                    return(
                                        <div>
                                        {siteCheck && <tr className="mr__content-worker">
                                            <div className="mr__content-data" style={{marginRight: "75px", justifyContent: "flex-start"}} ><p >{worker.name}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "36px"}}><p >{worker.type}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "20px"}}><p >{wReport?.site==="NA" ? worker.site : wReport?.site}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "15px"}}><p >{worker.phoneNo}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "50px"}}><p >{worker.doj.substring(0, 10)}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "30px"}}><p >{worker.uanNo}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "20px"}}><p >{worker.pfNo}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "50px"}}><p >{worker.esic}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "70px"}}><p >{wReport?.presentDays}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "60px"}}><p >{wReport?.otHrs}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "70px"}}><p >{worker.wagesRate}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "60px"}}><p >{worker.daRate}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "50px"}}><p >{worker.otRate}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "80px"}}><p >{wReport?.basicWages}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "40px"}}><p >{wReport?.daWages}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "30px"}}><p >{wReport?.otAmt}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "40px"}}><p >{wReport?.hra}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "40px"}}><p >{wReport?.totalWages}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "0px"}}><p >{wReport?.pf}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "0px"}}><p >{wReport?.esic}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "0px"}}><p >{wReport?.pt}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "50px"}}><p >{wReport?.lwf}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "120px"}}><p >{wReport?.adv}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "80px"}}><p >{wReport?.totDeduction}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "35px"}}><p >{wReport?.fPay}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "25px"}}><p >{worker.bank}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "70px"}}><p >{worker.accountNo}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "55px"}}><p >{worker.ifsc}</p></div>
                                            <div className="mr__content-data" style={{marginRight: "50px"}}><p >{worker.aadharNo}</p></div>                                    
                                            
                                    </tr>}
                                    </div>
                                    )
                                })}
                            
                            </tbody>
                        </table>
                    </div>


                   
                   
                    
                </div>
            </div>
        </div>
    )
}

export default MonthlyReport
