import React, {useRef, useState} from 'react';
import Navbar from '../Components/Navbar';
import Alert from '@material-ui/lab/Alert';
import { sitenames } from './sitenames';
//import { useStateValue } from '../StateProvider';

function Sites() {

    const [addSuccess, setAddSuccess] = useState(false);

    const siteRef = useRef();
    //const [{sites}, dispatch] = useStateValue();

    const handleAdd = () => {
        // dispatch({
        //     type: 'add-site',
        //     item: {
        //         site: siteRef.current.value ,
        //     }           
        // });

        let newSites = [...sitenames, siteRef.current.value];
        sitenames = newSites;

        setAddSuccess(true);
        console.log(sitenames);
    }


    return (
        <div className="cw">           
            <div className="cw__nav">
                <Navbar />
            </div>
            
            <div className="cw__container" style={{display: "flex", justifyContent: "center", width: "40vw", alignItems: "center", height: "80vh", marginTop: "50px"}}>
                <div className="att__content">
                    <h2 >Add a Site</h2>

                    <h3>Site Name</h3>
                    <input type="text" className="cw__inp" ref={siteRef} style={{marginBottom: "40px", height: "40px"}} />
                    
                    <button onClick={handleAdd}  className="cw__btn" style={{marginBottom: "10px"}} >Add Site</button>
                                       
                    {addSuccess && <Alert severity="success">Site Added</Alert>}
                    
                </div>

            </div>
        </div>
    )
}

export default Sites
