    const handlePrev = () => {
        
        let i = ind  - 1;
        setInd(i);
        console.log(ind);
    }

    const handleNext = () => {
        

        let k = ind  + 1;
        setInd(k);
        console.log(ind);
    }



    return (
        <div className="workers">
            <div className="workers__banner" style={{height: "350px"}}>
                <Navbar />
            </div>

            <div className="workers__title">
                <div className="mr__report-title" >
                    <ArrowLeftIcon disabled={prev} onclick={handlePrev} className="mr__title-arrow"  style={{fontSize: "70px", alignSelf: "center", color : "#9ae3e6"}} />
                    <h2>July's Report</h2>
                    <ArrowRightIcon disabled={next} onclick={handleNext} className="mr__title-arrow"  style={{fontSize: "70px", alignSelf: "center", color : "#9ae3e6"}} />
                    {/* <button onclick={handlePlus} >plus</button> */}
                    <p style={{color: "white"}} >{ind}</p>
                </div>
                
                <div >
                        <select name="site" className="workers__dropdown">
                            <option >Powai</option>
                            <option >Oberoi</option>
                            <option >DAISEC</option>
                        </select>
                </div>
            </div>

            <div className="workers__container" style={{overflowX: "scroll"}} >
                <div className="workers__content" >
                                       

                    <div >
                        <table className="workers__table">
                            <thead className="mr__content-header">
                            
                                <h3 style={{marginRight: "180px"}}>Name</h3>
