import React, {useState, useRef} from 'react';
import CloseIcon from '@material-ui/icons/Close';
import "./Attendance.css";
import axios from 'axios';

function Attendance({setTrig, worker}) {

    console.log(worker);

    const [month, setMonth] = useState('');
    const pdaysRef = useRef();
    const otRef = useRef();

    const [pf, setPf] = useState(0);
    const [pt, setPt] = useState(0);

    const handleMonth = (e) => {
        setMonth(e.target.value);
    }


    async function handleSave() {

        const basicWages = pdaysRef.current.value * worker.wagesRate;
        const daWages = pdaysRef.current.value * worker.daRate;
        const otAmt = otRef.current.value * worker.otRate;
        const hra = (basicWages + daWages) * 0.05;
        const totalWages = basicWages + daWages + otAmt + hra;
        if((basicWages + daWages) > 15000) {
            setPf(1800);
        }else {
            setPf((basicWages + daWages) * 0.12);
        }

        const esic = totalWages * 0.0075;

        if(totalWages <= 7500) {
            setPt(0);
        } else if (7500 < totalWages && totalWages <= 10000){
            setPt(1750);
        }
        else {
            setPt(200);
        }

        const totDeduction = pf + esic + pt + worker.adv;

        const fPay = totalWages - totDeduction;

        const reportData = {
	
			presentDays: pdaysRef.current.value,
			otHrs: otRef.current.value,
			basicWages: basicWages,
			daWages: daWages,
			otAmt: otAmt,
			hra: hra,
			totalWages: totalWages,
			pf: pf,
			esic: esic,
			pt: pt,
			lwf: 41,
			totDeduction: totDeduction,
			fPay: fPay		
        }

        let id = worker._id;

        await axios.post(`http://localhost:5000/users/update${month}Report/${id}`, reportData)
                .then(res => {console.log(res.data)})
                .catch(err => {console.log(err)})
    } 


    return (
        <div className="cw">
            
            <CloseIcon className="profile__back" onClick={() => setTrig(false)} style={{fontSize: "35px"}} />
            
            <div className="cw__container" style={{display: "flex", justifyContent: "center", width: "40vw", alignItems: "center"}}>
                <div className="att__content">
                    <h2 >Worker Name</h2>

                    <h3>Site</h3>
                    <div >
                            <select value={month} onChange={handleMonth} name="site" className="cw__dropdown">
                                <option >Jan</option>
                                <option >Feb</option>
                                <option >Mar</option>
                                <option >Apr</option>
                                <option >May</option>
                                <option >Jun</option>
                                <option >Jul</option>
                                <option >Aug</option>
                                <option >Sep</option>
                                <option >Oct</option>
                                <option >Nov</option>
                                <option >Dec</option>
                            </select>
                    </div>
                    <h3>Present days</h3>
                    <input type="number" className="cw__inp" ref={pdaysRef} style={{marginBottom: "40px", height: "40px"}} />
                    <h3>OT Hours</h3>
                    <input type="number" className="cw__inp" ref={otRef} style={{marginBottom: "40px", height: "40px"}}/>


                    <button onClick={handleSave} className="cw__btn" >Save</button>
                    
                </div>

            </div>
        </div>
    )
}

export default Attendance
